mapper.select:
SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}

	${sqlMap.where.toSql()}

		and a.data_date_key between #{qryStartTime} and #{qryEndTime}


ORDER BY ${sqlMap.order.toSql()}

SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}
join ems_meter b on a.device_id = b.meter_code

	${sqlMap.where.toSql()}

		and a.data_date_key between #{qryStartTime} and #{qryEndTime}


ORDER BY a.data_date_key, b.sort

SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}
where a.device_id = #{deviceId} and a.data_date_key = #{dataDateKey} and a.data_type = #{dataType}
ORDER BY a.data_date_key
LIMIT 1

select
	sum(eepc.positive_active_energy)
from ems_electric_power_consumption_statistics eepc
where eepc.corp_code = #{corpCode}
  and eepc.company_code = #{companyCode}
  and eepc.data_date_key BETWEEN #{startKey} and #{endKey}
  and eepc.data_type = #{temporalGranularity}

		and eepc.device_id in (

				#{deviceCode}

		)

select
	eepc.data_date_key as label,
	sum(eepc.positive_active_energy) as value
from ems_electric_power_consumption_statistics eepc
where eepc.corp_code = #{corpCode} and eepc.company_code = #{companyCode}
  and eepc.data_date_key BETWEEN #{startKey} and #{endKey}
  and eepc.data_type = #{temporalGranularity}

		and eepc.device_id in (

			#{deviceCode}

		)

group by eepc.data_date_key

SELECT ANY_VALUE(eepc.data_date_key) as data_date_key, ANY_VALUE(eepc.data_type) as data_type, sum( eepc.positive_active_energy ) AS positive_active_energy, sum( eepc.reverse_active_power ) AS reverse_active_power, sum( eepc.positive_reactive_energy ) AS positive_reactive_energy, sum( eepc.reverse_reactive_power ) AS reverse_reactive_power FROM ems_area ea JOIN ems_area_meter eam ON ea.area_code = eam.area_code JOIN ems_electric_power_consumption_statistics eepc ON eam.meter_code = eepc.device_id WHERE ea.corp_code = #{corpCode} AND ( ea.area_code = #{areaCode} OR ea.parent_codes LIKE CONCAT('%', #{areaCode}, '%' ))
and eepc.device_id in (
#{deviceCode}
)
AND eepc.data_date_key = #{dataDateKey} AND eepc.data_type = #{dataType}

select
	eepcs.data_date_key,
	sum(eepcs.positive_active_energy) as positive_active_energy,
	sum(eepcs.reverse_active_power) as reverse_active_power,
	sum(eepcs.positive_reactive_energy) as positive_reactive_energy,
	sum(eepcs.reverse_reactive_power) as reverse_reactive_power
from ems_area ea
	join ems_area_meter eam on ea.area_code = eam.area_code
	join ems_electric_power_consumption_statistics eepcs on eam.meter_code = eepcs.device_id
where ea.corp_code = #{corpCode}

		AND ( ea.area_code = #{areaCode} OR ea.parent_codes LIKE CONCAT('%', #{areaCode}, '%' ))

    and eepcs.data_date_key BETWEEN #{qryStartTime} and #{qryEndTime}

		and eepcs.device_id = #{deviceId}


		and eepcs.device_id in (

			#{deviceCode}

		)

    and eepcs.data_type = #{dataType}
group by eepcs.data_date_key

SELECT a.device_name as label, a.positive_active_energy as value from ems_electric_power_consumption_statistics a
${sqlMap.where.toSql()}
ORDER BY a.positive_active_energy desc
