mapper.select:
SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}

	${sqlMap.where.toSql()}

		and a.data_date_key between #{qryStartTime} and #{qryEndTime}


ORDER BY ${sqlMap.order.toSql()}

SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}
where a.office_code = #{officeCode} and a.device_id = #{deviceId} and a.data_date_key = #{dataDateKey} and a.data_type = #{dataType}
ORDER BY a.data_date_key
	LIMIT 1

SELECT ANY_VALUE(a.`office_code`) AS "officeCode", ANY_VALUE(a.`office_name`) AS "officeName", ANY_VALUE(a.`data_date_key`) AS "dataDateKey", ANY_VALUE(a.`data_type`) AS "dataType", SUM(a.`total_energy`) AS "totalEnergy", SUM(a.`cusp_time_energy`) AS "cuspTimeEnergy", SUM(a.`peak_time_energy`) AS "peakTimeEnergy", SUM(a.`fair_time_energy`) AS "fairTimeEnergy", SUM(a.`valley_time_energy`) AS "valleyTimeEnergy" FROM ${sqlMap.table.toSql()} join js_sys_office jso on a.office_code = jso.office_code
${sqlMap.where.toSql()}
and a.data_date_key between #{qryStartTime} and #{qryEndTime}
GROUP BY a.`office_code`,a.`data_date_key` ORDER BY a.`data_date_key`, jso.tree_sort
