mapper.select:
SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}

	${sqlMap.where.toSql()}

		and a.data_date between #{dataDateStart} and #{dataDateEnd}


ORDER BY ${sqlMap.order.toSql()}

SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}
join ems_meter b on a.device_id = b.meter_code

	${sqlMap.where.toSql()}

		and a.data_date between #{dataDateStart} and #{dataDateEnd}


ORDER BY a.data_date, b.sort

SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}
where a.device_id = #{deviceId} and a.data_date = #{dataDate} and a.data_type = #{dataType}
ORDER BY a.data_date
LIMIT 1

SELECT ${sqlMap.column.toSql()}
FROM ${sqlMap.table.toSql()}

	a.device_id = #{deviceId} and a.data_date = #{dataDate} and a.data_type = #{dataType}

ORDER BY a.data_date
LIMIT 1

select
	etspc.data_date,
	etspc.data_type,
	sum(etspc.total_energy) as total_energy,
	sum(etspc.cusp_time_energy) as cusp_time_energy,
	sum(etspc.peak_time_energy) as peak_time_energy,
	sum(etspc.fair_time_energy) as fair_time_energy,
	sum(etspc.valley_time_energy) as valley_time_energy
from ems_time_share_power_consumption etspc
where etspc.corp_code = #{corpCode} and etspc.company_code = #{companyCode}
  and etspc.data_date BETWEEN #{dataDateStart} and #{dataDateEnd}
  and etspc.data_type = #{dataType}

		and etspc.device_id in (

			#{deviceCode}

		)

group by etspc.data_date

select
	tmp.total_energy as totalEnergy,
	tmp.cusp_time_energy as cuspTimeEnergy,
	tmp.peak_time_energy as peakTimeEnergy,
	tmp.fair_time_energy as fairTimeEnergy,
	tmp.valley_time_energy as valleyTimeEnergy
from (
	 select
		 sum(etspc.total_energy) as total_energy,
		 sum(etspc.cusp_time_energy) as cusp_time_energy,
		 sum(etspc.peak_time_energy) as peak_time_energy,
		 sum(etspc.fair_time_energy) as fair_time_energy,
		 sum(etspc.valley_time_energy) as valley_time_energy
	 from ems_time_share_power_consumption etspc
	 where etspc.corp_code = #{corpCode}
	   and etspc.device_id = #{deviceId}
	   and etspc.data_date BETWEEN #{dataDateStart} and #{dataDateEnd}
	   and etspc.data_type = #{dataType}
) tmp

SELECT ANY_VALUE(eepc.data_date) as data_date, ANY_VALUE(eepc.data_type) as data_type, sum( eepc.total_energy ) AS total_energy, sum( eepc.cusp_time_energy ) AS cusp_time_energy, sum( eepc.peak_time_energy ) AS peak_time_energy, sum( eepc.fair_time_energy ) AS fair_time_energy, sum( eepc.valley_time_energy ) AS valley_time_energy FROM ems_area ea JOIN ems_area_meter eam ON ea.area_code = eam.area_code JOIN ems_time_share_power_consumption eepc ON eam.meter_code = eepc.device_id WHERE ea.corp_code = #{corpCode} AND ( ea.area_code = #{areaCode} OR ea.parent_codes LIKE CONCAT('%', #{areaCode}, '%' ))
and eepc.device_id in (
#{deviceCode}
)
AND eepc.data_date = #{dataDate} AND eepc.data_type = #{dataType}
